catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading || newValue === '' || newValue == oldValue) {
    return;
  }

  // Instantiate the GetEmailAddress Script Include 
  var getEmailAddr = new GlideAjax('GetEmailAddressYa');
  // Specify the getEmail method
  getEmailAddr.addParam('sysparm_name','getEmail');
  // Pass the Requested for sys_id
  getEmailAddr.addParam('sysparm_userID', g_form.getValue('nombre_de_empleado'));
  // Send the request to the server
  getEmailAddr.getXML(populateEmailField);


  // When the response is back from the server
  function populateEmailField(response){
    // Extract the email address from the response, clear any value from the email field, 
    // set new value in the email field
    var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
    g_form.clearValue('correo_electronico');
    g_form.setValue('correo_electronico',emailFromScriptInclude);
	g_form.setReadOnly('correo_electronico',true);
  }

   //Type appropriate comment here, and begin script below

}
